INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('186', '이것은 비추다와 과거를 뜻하는 영어 단어의 합성어로 영화나 드라마에서 현재로부터 과거로 거슬러 가는 장면이나 기법을 말합니다. 이것을 활용한 대표적인 영화로는 타이타닉과 포레스트 검프가 있는데요. 이것은 무엇일까요?', '', 'ㅍㄹㅅㅂ', '','COMMON','TEXT');
