INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('187', '어찌하여 아침인데 노란 등불 들고 나오셨나요 이는 나태주 시인님이 이것을 보고 지은 시입니다. 이것은 여름에 노랗게 꽃을 피우며 꽃이 지고 난 뒤 열리는 씨앗으로 기름을 만들기도 하는데요. 낮에는 피지 않고 밤에 꽃이 핀다 하여 이름이 붙여진 이 식물은 무엇일까요?', '', 'ㄷㅁㅇㄲ', '','COMMON','TEXT');
