INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('188', '이것은 조선 시대에 편지를 쓸 때 끝맺음을 하기 위해 사용한 말입니다. 편지를 이만 마무리하겠다는 뜻으로. 무척 급하고 바쁜 모양을 뜻하는 이것을 쓴 것인데요. 2음절의 한자어인 이것은 무엇일까요?', '', 'ㅊㅊ', '','COMMON','TEXT');
