INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('189', '국립국어원에서는 스펙업을 대신할 우리말 순화어로 이것 쌓기를 선정했습니다. 이것은 일정한 자격이나 조건을 갖춘다는 뜻의 우리말과 헤아리다를 뜻하는 한자어의 합성어인데요. 스스로 일을 해낼 만한 능력을 뜻하는 이것은 무엇일까요?', '', 'ㄲㄴ', '','COMMON','TEXT');
