INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('190', '원래 이것은 영국 정부의 공식 보고서를 가리키는 말이었습니다. 이것은 1920년대 영국 정부가 외교 정책을 알리는 보고서의 표지 색깔 때문에 이름이 붙여졌다고 하는데요. 오늘날 정부가 정치 경제 등 각 분야의 현상을 분석해 국민에게 정보를 알려주는 문서를 뜻하는 이것은 무엇일까요?', '', 'ㅂㅅ', '','COMMON','TEXT');
