INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('191', '이것은 함께 달리다라는 뜻의 그리스어에서 유래된 것으로, 의학에서는 여러 증상이 동시에 나타나지만 원인을 알 수 없는 상태를 뜻하는 말로 쓰이는데요. 흔히 어떤것을 좋아하는 현상이 사람들 사이에 빠르게 퍼져 나가는 것을 뜻하는 이것은 무엇일까요?', '', 'ㅅㄷㄹ', '','COMMON','TEXT');
