INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('192', '이것은 제 2차 세계 대전 당시 공군에서 쓰던 용어로 비행 직전 조종사에게 내리는 간단한 명령이라는 뜻입니다. 오늘날은 요점을 간추려 간단하게 보고하거나 설명하는 것을 이것이라 하는데요. 이것은 무엇일까요?', '', 'ㅂㄹㅍ', '','COMMON','TEXT');
