INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('193', '우리나라에 있는 최대 규모의 해양과학조사선의 이름은 이 인물의 이름을 따 지었다고 합니다. 이 인물은 한국 역사상 최초로 해양 영토를 개척한 인물이자 울릉도와 독도를 우리나라 영역으로 편입시킨 신라 시대의 장군인데요. 이 인물은 누구일까요?', '', 'ㅇㅅㅂ', '','COMMON','TEXT');
