INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('195', '이것은 원래 일정한 거처 없이 떠돌아다니던 체코의 서부 지역 출신 사람을 가리키는 말이었는데요. 이후 19세기 낭만주의가 유행하면서 프랑스 소설가인 앙리 뮈르제는 이것을 가난하지만 자유로운 삶을 살며, 예술을 추구하는 사람들이라 표현했다고 합니다. 오늘날 자유분방한 삶을 사는 예술가를 뜻하는 이것은 무엇일까요?', '', 'ㅁㅎㅁㅇ', '','COMMON','TEXT');
