INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('196', '이것은 돌을 갈고 닦아 표면을 반질반질하게 한다는 뜻입니다. 돌의 거친 표면을 매끈하게 만드는 것을 빗대어 오늘날에는 가진 것이 닳아 없어질 만큼 열중한다는 의미로 쓰이는데요. 흔히 학문이나 기술을 배워 익힐 때 이것하다라고 표현합니다. 이것은 무엇일까요?', '', 'ㅇㅁ', '','COMMON','TEXT');
