INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('197', '이것은 중국의 역사서인 후안서에서 유래된 말입니다. 어머니의 무덤 옆을 지키며 살던 한 효자의 집 앞에 두 나무의 가지가 하나로 엉켜 자란데서 이것이란 말이 생겨났는데요. 이후 이것은 효의 상징으로 여겨지다 오늘날에는 연인 간의 깊은 사랑을 비유하는 말로 쓰입니다. 이것은 무엇일까요?', '', 'ㅇㄹㅈ', '','COMMON','TEXT');
