INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('198', '이것은 언제 어디서나 존재한다는 뜻의 라틴어에서 유래된 말로, 현실 세계와 인터넷이라는 가상 세계의 결합을 말합니다. 1991년 컴퓨터 마크 와이저가 미래를 예측한 논문에 이것이란 용어를 처음 사용하며 알려졌는데요. 이것은 무엇일까요?', '', 'ㅇㅂㅋㅌㅅ', '','COMMON','TEXT');
