INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('200', '이것은 라틴어로 경쟁하다 또는 협력하다라는 뜻인데요. 음악에서는 오케스트라와 독주 악기가 서로 경쟁과 협력을 통해 합주하는 장르를 가리켜 이것이라고 합니다. 주로 3악장으로 구성돼있으며, 대화 형식으로 서로 응답해 나가면서 연주하는 게 특징인 이것은 무엇일까요?', '', 'ㅋㅊㄹㅌ', '','COMMON','TEXT');
