INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('201', '이것은 넓게 펼쳐진 땅과 형태를 뜻하는 라틴어의 합성어인데요. 오늘날 이것은 다양한 시스템이나 서비스를 제공하기 위한 기초적인 틀이라는 의미로 쓰이고 있습니다. 전 구글 회장인 에릭 슈미트는 일등 이것이 되는 것을 기업 목표로 삼기도 했는데요. 이것은 무엇일까요?', '', 'ㅍㄹㅍ', '','COMMON','TEXT');
