INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('202', '이것은 물고기나 새를 잡는 그물이라는 뜻으로 바다는 물론 하늘에도 그물을 쳐 모두 잡아들인다는 뜻인데요. 오늘날에는 일정한 범위 안에 있는 것들을 모두 포함한다는 의미로 쓰이는 이것은 무엇일까요?', '', 'ㅁㄹ', '','COMMON','TEXT');
