INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('203', '이것은 원래 밥이 몹시 질어 질퍽질퍽한 상태를 가리키는 말인데요. 오늘날 이것은 일이 엉망이 되어 갈피를 잡기 어렵게 된 상태라는 뜻으로도 쓰입니다. 흔히 몸이 지쳐 축 늘어져 있는 모습을 이것에 비유하기도 하는데요. 이것은 무엇일까요?', '', 'ㄱㅈ', '','COMMON','TEXT');
