INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('204', '이것은 원래 소의 등골이나 머릿골을 맑은 장국에 넣어 끓인 국을 가리키는 말입니다. 이후 이것은 상하다라는 뜻을 가진 말과 소리가 비슷하다는 이유로 손해를 보거나 낭패를 당한다는 뜻으로 쓰이게 됐다고 하는데요. 이것은 무엇일까요?', '', 'ㄱㅌ', '','COMMON','TEXT');
