INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('205', '이것은 원래 채소나 생선을 소금에 절이는 것을 뜻하는 말입니다. 간이 완전히 들지 않고 적당히 들었다는 데서 오늘날 됨됨이가 변변치 못한 사람을 이것이라 하는데요. 이것은 무엇일까요?', '', 'ㅇㄱㅇ', '','COMMON','TEXT');
