INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('206', '1997년 미국 과학자들에 의해 처음 입증된 이것은 글루타메이트라는 성분에 의해 느껴진다고 합니다. 이후 이것은 단맛, 짠맛, 쓴맛, 신맛에 이어 새롭게 추가된 제 5의 맛으로 인정되었습니다. 주로 해산물이나 고기에서 강하게 느낄 수 있는 이 맛은 무엇일까요?', '', 'ㄱㅊㅁ', '','COMMON','TEXT');
