INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('207', '이것은 프랑스어로 존경이나 경의를 뜻하는 말입니다. 주로 영화에서 다른 작품의 주요 장면이나 대사를 인용하는 것을 이것이라 하는데요. 자신이 존경하는 감독이나 작품에 대한 경의를 뜻하는 이것은 무엇일까요?', '', 'ㅇㅁㅈ', '','COMMON','TEXT');
