INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('208', '이것은 원래 바람에 나무가 쓰러진다는 뜻으로 어떤 현상이나 사상이 사회에 널리 퍼지는 것을 이르는 말입니다. 흔히 사람이나 사물이 한 시대에 큰 영향력을 끼쳤을 때 시대를 이것했다라도 표현하기도 하는데요. 이것은 무엇일까요? ', '', 'ㅍㅁ', '','COMMON','TEXT');
