INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('209', '이것은 중국 춘추 시대 때 썼던 군사 작전에서 유래된 말인데요. 당시 전쟁에서 전차와 전차 사이의 간격을 병사들로 실로 꿰매듯 빈틈없이 메우는 전술을 썼다고 합니다. 오늘날에는 잘못된 것을 해결하지 않고 임시 방편으로 보완한다는 의미로 쓰이고 있는 이것은 무엇일까요? ', '', 'ㅁㅂㅊ.ㅁㅂ', '','COMMON','TEXT');
