INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('211', '국제기구인 UN의 로고는 이 나무가 세계지도를 감싸고 있는 모양입니다. 그리스 신화에서 아테나 여신은 이 나무를 가리켜 밤에도 세상을 밝혀주고 상처를 치료해주는 귀한 나무라고 했는데요. 지중해식 요리에 필수 재료로 쓰이는 이것은 무엇일까요?', '', 'ㅇㄹㅂ', '','COMMON','TEXT');
