INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('212', '인류 역사상 가장 혁신적인 발명품으로 꼽히는 에어컨은 1902년 이 인물에 의해 탄생했습니다. 미국의 공학자인 이 인물은 기차역의 안개를 보고 온도와 습도를 제어하는 에어컨의 원리를 떠올렸는데요. 에어컨의 대중화를 위해 본인의 이름을 딴 회사를 만든 이 인물은 누구일까요?', '', 'ㅇㄹㅅ ㅋㄹㅇ', '','COMMON','TEXT');
