INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('213', '이것은 아주 얇은 얼음이라는 뜻의 한자어입니다. 근소한 차이나 우열을 가리기 어려운 상태를 비유하는 말인데요. 흔히 긴장감 넘치는 막상막하의 승부를 가리켜 이것이라고 하는데요. 이것은 무엇일까요?', '', 'ㅂㅂ', '','COMMON','TEXT');
