INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('214', '이것은 원래 특정한 작업을 실행하기 위한 명령을 뜻하는 말입니다. 스포츠에서는 선수가 최고의 성적을 내기 위해 일정하지 않은 자신만의 고유한 동작을 뜻하는 말로 쓰이고 있는데요. 경기력을 높이는데 아주 중요한 심리기술 중 하나인 이것은 무엇일까요?', '', 'ㄹㅌ', '','COMMON','TEXT');
