INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('215', '예로부터 우리나라는 이 새를 사냥감으로 많이 이용했다는데요. 조선 시대 백과사전인 임원경제지에는 이 새의 울음소리를 흉내 내 이 새가 가까이 날아왔을 때 활을 쏘면 쉽게 잡을 수 있다고 기록되어 있습니다. 쏜살같이 날아오를 때 화살로 잡았다고 해서 이 새의 한자어에는 화살 시(矢)자가 포함되어 있는데요. 이 새는 무엇일까요?', '', 'ㄲ', '','COMMON','TEXT');
