INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('217', '이것은 캐나다의 이론가 마셜 매클루언이 처음 만든 단어입니다. 그는 전자매체로 인해 지구가 하나의 부족처럼 연결된다는 개념을 이것이라 표현했는데요. 오늘날에는 국경의 경계가 허물어진 상태를 비유하는 말로도 쓰이고 있습니다. 흔히 올림픽을 이것 대축제라고도 하는데요. 이것은 무엇일까요?', '', 'ㅈㄱㅊ', '','COMMON','TEXT');
