INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('218', '하계 올림픽 메달의 앞면에는 이 인물이 새겨져 있습니다. 이는 1928년 국제올림픽위원회에서 하계 올림픽 메달의 표준 디자인으로 월계관을 쓴 이 인물을 새겨야 한다는 규정이 있기 때문인데요. 하계 올림픽 메달 앞면에 새겨진 인물로 그리스 신화 속 승리의 여신으로 불리는 이 인물은 누구일까요?', '', 'ㄴㅋ', '','COMMON','TEXT');
