INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('219', '이것은 고대 그리스의 극작가가 쓴 비극 아가멤논에 등장한 물건입니다. 트로이 전쟁에서 승리한 아가멤논 장군이 10년 만에 집에 돌아오자, 그의 아내는 환영과 존경하는 마음을 담아 이것을 준비했닫고 하는데요. 오늘날 각종 행사에서 귀빈을 맞이할 때 쓰이는 이것은 무엇일까요?', '', 'ㄹㄷㅋㅍ', '','COMMON','TEXT');
