INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('220', '이것은 가문이 좋은 사람이라는 뜻의 라틴어에서 유래된 말로 16세기 영국에서는 지식과 품위를 중요시하는 특정 신분을 이것이라 불렀는데요. 이후 이들은 사회의 엘리트로서 시민혁명과 산업혁명에서 중요한 역할을 했다고 합니다. 오늘날 예절을 중요시하는 사람을 뜻하는 이것은 무엇일까요?', '', 'ㅈㅌㅁ', '','COMMON','TEXT');
