INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('221', '이것은 원래 바둑에서 승패를 좌우하는 결정적인 수를 뜻하는 말인데요. 오늘날에는 매우 불리한 상황을 뒤집을 수 있는 마지막 시도를 가리키는 말로 사용되고 있습니다. 흔히 스포츠 경기에서 선수가 과감한 선택을 했을 때 이것을 던지다라고 표현하는데요. 이것은 무엇일까요?', '', 'ㅅㅂㅅ', '','COMMON','TEXT');
