INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('222', '이것은 호랑이가 눈을 부릅뜨고 먹이를 노려본다는 뜻의 사자성어입니다. 먹잇감을 향해 뚫어지게 쳐다보며 기회를 노리는 호랑이의 모습에 빗대어 흔히 남의 것을 빼앗고자 기회를 엿보는 모습을 가리켜 이것이라 하는데요. 이것은 무엇일까요?', '', 'ㅎㅅㅌㅌ', '','COMMON','TEXT');
