INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('223', '이것은 승리의 기세를 타고 계속 말을 몰고 나간다는 뜻의 사자성어입니다. 전쟁에서 한번 이기고 나면 기세가 등등해져 그 기운으로 나머지 전투도 기운차게 임하게 된다는 것인데요. 흔히 거침없이 성공의 가도를 달리는 모습을 비유하는 말로 쓰이는 이것은 무엇일까요?', '', 'ㅅㅅㅈㄱ', '','COMMON','TEXT');
