INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('225', '이것은 유교 경전 중 하나인 서경에 처음 등장하는 말인데요. 탕왕은 향락에 빠져서 나라를 돌보지 않는 걸왕을 몰아내며 임금이 덕이 없어 백성들이 이것에 빠졌도다라는 말을 했다고 합니다. 이것은 당시 백성들의 힘든 삶을 진흙탕과 숯불에 비융한 말인데요. 이것은 무엇일까요?', '', 'ㄷㅌ', '','COMMON','TEXT');
