INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('226', '이것은 고대 그리스에서 흉조로 여겨진 새의 이름에서 유래됐습니다. 이 새는 목을 180도 비틀 수 있으며 기이한 소리를 내는 습성이 있어 길흉을 점칠 때 활용됐다고 하는데요. 스포츠 선수들이 어떤 사물이나 행동을 연관지어 불길한 예감을 가지는 현상을 이것이라 합니다. 이것은 무엇일까요?', '', 'ㅈㅋㅅ', '','COMMON','TEXT');
