INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('339', '올해도 김치를 (담갔다/담궜다).에서 ( ) 안에 들어갈 알맞은 맞춤법은? ', '담그다는 어간의 끝 ㅡ가 줄어 담그-+-아→ 담가, 담그-+-았-→ 담갔-, 담갔다로 적습니다.', '', '','GRAMMAR','TEXT');
