INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('348', '우리는 다함께 (목거지/모꼬지) 자리로 들어섰다.에서 ( ) 안에 들어갈 알맞은 맞춤법은?', '놀이나 잔치 또는 그 밖의 일로 여러 사람이 모이는 일을 일컫는 순우리말은 모꼬지이며, 목거지는 모꼬지의 방언입니다.', '', '','GRAMMAR','TEXT');
