INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('349', '그에게 땀방울이 (송골송골/송글송글) 맺혔다.에서 ( ) 안에 들어갈 알맞은 맞춤법은?', '땀이나 소름, 물방울 따위가 살갗이나 표면에 잘게 많이 돋아나있는 모양은 송골송골로 씁니다.', '', '','GRAMMAR','TEXT');
