INSERT INTO `quizdot`.`quiz` (`id`, `question`, `description`, `hint`, `image_path`, `category`, `question_type`) VALUES ('350', '(시월/십월)에는 너에게 편지를 쓸게.에서 ( ) 안에 들어갈 알맞은 맞춤법은?', '한자어에서 본음으로도 나도 속음으로도 나는 것은 소리에 따라 적을 수 있으므로, 소리 나는 시월로 표기합니다.', '', '','GRAMMAR','TEXT');
